mapper.select:
SELECT p.*,NVL(s.checkB,0),s.review, NVL(stock,0) stock, s.score
FROM product p, starNum s,product_stock ps
WHERE p.prod_no=s.prod_no(+) AND p.prod_no=ps.prod_no(+) AND p.prod_no= #{value}

mapper.update:
UPDATE product p
prod_name=#{prodName:VARCHAR}, prod_detail=#{prodDetail:VARCHAR}, manufacture_day =#{manuDate:VARCHAR}, price = #{price:NUMERIC}, image_file=#{fileName:VARCHAR}
WHERE p.prod_no = #{prodNo}

mapper.select:
SELECT * FROM (SELECT inner_table.*, ROWNUM AS row_seq FROM (SELECT p.prod_no,prod_name,price,reg_date,image_file, stock FROM product p, product_stock ps WHERE p.prod_no=ps.prod_no(+)
AND p.prod_no=#{search.searchKeyword}
AND p.prod_name LIKE #{search.searchKeyword}
AND price BETWEEN #{rowPrice} AND #{highPrice} ORDER BY price
ORDER BY p.prod_no
ORDER BY prod_name
ORDER BY price
ORDER BY reg_date
ORDER BY stock
)inner_table WHERE ROWNUM <= #{search.endRowNum}) WHERE row_seq BETWEEN #{search.startRowNum} AND #{search.endRowNum}

	SELECT COUNT(*)
  	FROM(	SELECT p.prod_no,prod_name,price,reg_date,image_file, stock
					FROM product p, product_stock ps 
					WHERE p.prod_no=ps.prod_no(+)
					
						
							AND p.prod_no=#{search.searchKeyword}
						
						
							AND p.prod_name LIKE #{search.searchKeyword}
						
						
							AND price BETWEEN #{rowPrice} AND #{highPrice}
						
					 ) countTable
